const { spawn } = require('child_process');

// Функция для выполнения команды и ожидания её завершения
function runCommand(command, args) {
    return new Promise((resolve, reject) => {
        const process = spawn(command, args);
        
        process.stdout.on('data', (data) => {
            // console.log(`${data}`);
        });
        
        process.stderr.on('data', (data) => {
            console.error(`Error: ${data}`);
        });
        
        process.on('close', (code) => {
            if (code === 0) {
                resolve(`Команда завершена с кодом ${code}`);
            } else {
                reject(`Команда завершена с ошибкой с кодом ${code}`);
            }
        });
    });
}
let p = '';
// p = 'FFFFFFFFFFFF0123456789012345';
let i = 0;
// Функция для выполнения команд подряд
async function runCommands() {
    try {
        ++i;
        let str = i+'';
        str = str.slice(-1);
        console.log(i);
        await runCommand('./sender', ['eth0', [p, str].join('')]);
    } catch (error) {
        console.error('Произошла ошибка при выполнении команды: ', error);
    }
}

// setInterval(f => runCommands(), 100);
runCommands();
